mapper.select:
SELECT productNo, productCatCode, productName, productDetail, productPrice, productStock
  FROM product
 WHERE productNo=#{productNo}

SELECT memberName, memberCheck, memberId
  FROM member
 WHERE memberId=#{memberId} and memberPw=#{memberPw}

SELECT productName, salesCount, productPrice 
FROM cart
WHERE memberId=#{id}

SELECT memberId, memberName, memberPhone, memberAddress1, memberAddress2, memberJoinDate, memberBirthDate, memberGender, memberCheck
FROM member
WHERE memberId=#{memberId}
